package Basic;

import java.util.Iterator;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 0;

		while (n <= 10) {
			System.out.print(n+" ");
			n++;

		}
		
		System.out.println(); 
		for (int i = 0; i <=10; i++) {
			System.err.print(i+" "); 
		}  
	}

}
