package Interview;

import java.util.ArrayList;

public class ArrayList123 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	
	ArrayList lst = new ArrayList();
	
	System.out.println(lst);

	System.out.println(lst.size());
	
	lst.add(10);
	lst.add(20);
	lst.add(30);
	
	System.out.println(lst.size());
	
	
	lst.add("Vieky");
	lst.add("Siya");
	
	System.out.println(lst.size());
	
	lst.add('a');
	lst.add(21.5f);
	System.out.println(lst);
	
	lst.remove(2);

	System.out.println(lst);

	System.out.println(lst.size());

	
		
		
	}

}
